// Get the raw text
let raw = $input.first().json.text;

// Remove code block markers like ```json and ```
raw = raw.replace(/```json|```/g, '').trim();

// Parse the inner JSON safely
let data;
try {
  data = JSON.parse(raw);
} catch (e) {
  data = { title: null, post: null, error: "Invalid JSON from LLM" };
}


// Return clean destructured output
return [
  {
    json: {data
    }
  }
];
